tinymce.PluginManager.add('math_editor', function(editor, url) {
    editor.addButton('math_editor',
        {title       : 'MathQuill Based Editor',
         image       : url + '/img/math.png',
         onclick     : function() {
		editor.windowManager.open({
   		title: "Math Editor",
		url: url+'/index.html',
		width: 500,
		height: 400
		});

}});
});
